package Learning;import org.testng.annotations.Test;import java.util.Arrays;public class ReverseWithoutSpaceSwapping {    static String str = "String it";@Test    public void reverseIt(){        char[] carr = str.toCharArray();        int low = 0;        int high = carr.length-1;    System.out.println(Arrays.toString(carr));     for(int i=0; i<carr.length;i++){      while (low < carr.length / 2) {        char temp = carr[low];        carr[low] = carr[high];        carr[high] = temp;        low++;        high--;         }     }        System.out.println(Arrays.toString(carr));    }}