package Learning;import java.util.Arrays;public class mergeTwoArrays {	static  int[] arr1 = {1,3,5,6,7,9};	static int[] arr2 = {2,4,8,11,14};		static int n = 0;	static int[] arr3 = new int[n];	public static void main(String[] args){		mergeArrays(arr1,arr2);	}		public static void mergeArrays(int[] arr1, int[] arr2){		int x=0;		if(arr1.length<arr2.length){			x = arr1.length;		}else{			x = arr2.length;		}		int k = 0;		for(int i=0;i<x; i++){						if (arr1[i] < arr2[i]) {				arr3[n] = arr1[i];				n++;				k=i;			} else {				arr3[n] = arr2[i];				n++;				k=i;			}		}				while(arr1.length>x){			arr3[n] = arr1[k+1];			k++;			n++;		}		while(arr2.length>x){			arr3[n] = arr2[k+1];			k++;			n++;		}		System.out.println(Arrays.toString(arr3));	}}